mapper.select:
select *
FROM jsh_depot_item
where 1=1


    and name like #{bindName}


    and type = #{type}



    and remark like #{bindRemark}

and ifnull(delete_flag,'0') !='1'

    limit #{offset},#{rows}

SELECT
COUNT(id)
FROM jsh_depot_item
WHERE 1=1


    and name like #{bindName}


    and type = #{type}



    and remark like #{bindRemark}

and ifnull(delete_flag,'0') !='1'

select m.id MId, me.bar_code, m.name MName, m.mfrs MMfrs, m.model MModel, m.standard MStandard, m.other_field1 MOtherField1,m.other_field2 MOtherField2,m.other_field3 MOtherField3, concat_ws('', m.unit, u.basic_unit) MaterialUnit, m.color MColor, u.name unit_name, (select purchase_decimal from jsh_material_extend me where me.material_id=m.id and me.default_flag=1 and ifnull(me.delete_Flag,'0') !='1' limit 0,1) purchase_decimal from jsh_material m left join jsh_depot_item di on di.material_id=m.id and ifnull(di.delete_Flag,'0') !='1' left join jsh_material_extend me on me.id=di.material_extend_id and ifnull(me.delete_Flag,'0') !='1' left join jsh_depot_head dh on di.header_id=dh.id and ifnull(dh.delete_flag,'0') !='1' left join jsh_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' where 1=1
and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and dh.oper_time <= #{endTime}
and ifnull(m.delete_flag,'0') !='1' group by m.id order by m.id desc
limit #{offset},#{rows}

select ifnull(sum(basic_number),0) as BasicNumber from jsh_depot_item di,jsh_depot_head dh
where di.header_id = dh.id
and dh.type=#{type} and dh.sub_type=#{subType}
and di.material_id =#{MId}
and dh.oper_time >= #{beginTime}
and dh.oper_time <= #{endTime}
and ifnull(dh.delete_flag,'0') !='1'
and ifnull(di.delete_flag,'0') !='1'

select ifnull(sum(all_price),0) as AllPrice from jsh_depot_item di,jsh_depot_head dh
where di.header_id = dh.id
and dh.type=#{type} and dh.sub_type=#{subType}
and di.material_id =#{MId}
and dh.oper_time >= #{beginTime}
and dh.oper_time <= #{endTime}
and ifnull(dh.delete_flag,'0') !='1'
and ifnull(di.delete_flag,'0') !='1'

select ifnull(sum(discount_last_money),0) as allMoney from jsh_depot_head dh
where 1=1
and dh.type=#{type} and dh.sub_type=#{subType}
and dh.oper_time >= #{beginTime}
and dh.oper_time <= #{endTime}
and ifnull(dh.delete_flag,'0') !='1'

select ifnull(sum(di.basic_number),0) stockCheckSum from jsh_depot_head dh
left JOIN jsh_depot_item di on dh.id=di.header_id
where 1=1

    and di.material_id=#{mId}

and dh.sub_type='盘点复盘'

    and di.depot_id=#{depotId}


    and dh.oper_time >= #{beginTime}


    and dh.oper_time <= #{endTime}

and ifnull(dh.delete_flag,'0') !='1'
and ifnull(di.delete_flag,'0') !='1'

select sum(case when dh.type='入库' and di.depot_id=#{depotId} then di.basic_number else 0 end) as inTotal, sum(case when dh.type='出库' and dh.sub_type!='调拨' and di.depot_id=#{depotId} then di.basic_number else 0 end) as outTotal, sum(case when dh.sub_type='调拨' and di.another_depot_id=#{depotId} then di.basic_number else 0 end) as transfInTotal, sum(case when dh.sub_type='调拨' and di.depot_id=#{depotId} then di.basic_number else 0 end) as transfOutTotal, sum(case when dh.sub_type='组装单' and di.material_type='组合件' and di.depot_id=#{depotId} then di.basic_number else 0 end) as assemInTotal, sum(case when dh.sub_type='组装单' and di.material_type='普通子件' and di.depot_id=#{depotId} then di.basic_number else 0 end) as assemOutTotal, sum(case when dh.sub_type='拆卸单' and di.material_type='普通子件' and di.depot_id=#{depotId} then di.basic_number else 0 end) as disAssemInTotal, sum(case when dh.sub_type='拆卸单' and di.material_type='组合件' and di.depot_id=#{depotId} then di.basic_number else 0 end) as disAssemOutTotal from jsh_depot_head dh left join jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1' where 1=1 and ifnull(dh.delete_flag,'0') !='1' and di.material_extend_id=#{meId} and ifnull(di.sku,'') !=''
and dh.oper_time >= #{beginTime}
and dh.oper_time <= #{endTime}

select sum(case when dh.type='入库' and di.depot_id=#{depotId} then di.basic_number else 0 end) as inTotal, sum(case when dh.type='出库' and dh.sub_type!='调拨' and di.depot_id=#{depotId} then di.basic_number else 0 end) as outTotal, sum(case when dh.sub_type='调拨' and di.another_depot_id=#{depotId} then di.basic_number else 0 end) as transfInTotal, sum(case when dh.sub_type='调拨' and di.depot_id=#{depotId} then di.basic_number else 0 end) as transfOutTotal, sum(case when dh.sub_type='组装单' and di.material_type='组合件' and di.depot_id=#{depotId} then di.basic_number else 0 end) as assemInTotal, sum(case when dh.sub_type='组装单' and di.material_type='普通子件' and di.depot_id=#{depotId} then di.basic_number else 0 end) as assemOutTotal, sum(case when dh.sub_type='拆卸单' and di.material_type='普通子件' and di.depot_id=#{depotId} then di.basic_number else 0 end) as disAssemInTotal, sum(case when dh.sub_type='拆卸单' and di.material_type='组合件' and di.depot_id=#{depotId} then di.basic_number else 0 end) as disAssemOutTotal from jsh_depot_head dh left join jsh_depot_item di on dh.id=di.header_id and ifnull(di.delete_flag,'0') !='1' where 1=1 and ifnull(dh.delete_flag,'0') !='1' and di.material_id=#{mId}
and dh.oper_time >= #{beginTime}
and dh.oper_time <= #{endTime}

select dep.id,dep.header_id,dep.material_id,dep.material_unit,dep.oper_number, dep.basic_number,dep.unit_price,dep.tax_unit_price,dep.all_price,dep.remark, dep.depot_id,dep.another_depot_id,dep.tax_rate, dep.tax_money,dep.tax_last_money,dep.material_type from jsh_depot_item dep,jsh_material mat where 1=1
and dep.header_id = #{depotheadId}
and dep.material_id=mat.id
and mat.enable_serial_number = #{enableSerialNumber}
and ifnull(dep.delete_flag,'0') !='1' and ifnull(mat.delete_flag,'0') !='1'

mapper.delete:
delete from jsh_depot_item
where 1=1
and header_id in
(

    #{depotheadId}

)

mapper.update:
update jsh_depot_item set delete_flag='1' where 1=1 and header_id in (
#{depotheadId}
)

update jsh_depot_item
set delete_flag='1'
where 1=1
and id in (

    #{id}

)

mapper.select:
select
from jsh_depot_item where 1=1 and depot_id in (
#{depotId}
) and ifnull(delete_flag,'0') !='1'

select
from jsh_depot_item where 1=1 and material_id in (
#{materialId}
) and ifnull(delete_flag,'0') !='1'

select m.id MId, m.name MName, me.bar_code, m.mfrs MMfrs, m.model MModel, m.standard MStandard,m.color MColor, m.other_field1 MOtherField1,m.other_field2 MOtherField2,m.other_field3 MOtherField3, m.unit MaterialUnit, u.name unit_name,mcs.current_number,ifnull(m.safety_stock,0) safetystock, (ifnull(m.safety_stock,0)-mcs.current_number) linjieNumber from jsh_material m left join jsh_material_extend me on me.material_id=m.id and ifnull(me.delete_Flag,'0') !='1' left join jsh_material_current_stock mcs on mcs.material_id=m.id and ifnull(mcs.delete_Flag,'0') !='1' left join jsh_depot_item di on di.material_id=m.id and ifnull(di.delete_Flag,'0') !='1' left join jsh_depot_head dh on di.header_id=dh.id and ifnull(dh.delete_flag,'0') !='1' left join jsh_unit u on m.unit_id=u.id and ifnull(u.delete_Flag,'0') !='1' where 1=1 and me.default_flag=1 and ifnull(m.delete_flag,'0') !='1' and ifnull(m.safety_stock,0) > mcs.current_number
and (me.bar_code like #{bindKey} or m.name like #{bindKey} or m.standard like #{bindKey} or m.model like #{bindKey})
and mcs.depot_id= #{depotId}
group by m.id,m.name, m.model, m.unit, m.color, u.name order by linjieNumber desc
limit #{offset},#{rows}
